select 
,t3.name as group_name,t2.DIC_PENAL_TYPE_KEY,t2.CRIME_INFO
from SF_CULPRIT t1 left join SF_CULPRIT_JUDGMENT t2 on (t1.ID=t2.CULPRIT_ID) left join sys_group t3 on (t1.group_id=t3.id)
where t1.ID = #{id,jdbcType=VARCHAR}

select 
,t3.name as group_name,t2.DIC_PENAL_TYPE_KEY,t2.CRIME_INFO,t4.DIC_MONITOR_GRADE_KEY as MONITOR_GRADE
from SF_CULPRIT t1 left join SF_CULPRIT_JUDGMENT t2 on (t1.ID=t2.CULPRIT_ID) left join sys_group t3 on (t1.group_id=t3.id)
left join SF_GRADE_CHANGE t4 on(t1.ID=t4.CULPRIT_ID)
where t1.ID = #{id,jdbcType=VARCHAR}

select 
,t3.name as group_name,t2.DIC_PENAL_TYPE_KEY,t2.CRIME_INFO,t5.ACCOUNTNAME
from SF_CULPRIT t1 left join SF_CULPRIT_JUDGMENT t2 on (t1.ID=t2.CULPRIT_ID) left join sys_group t3 on (t1.group_id=t3.id) left join sys_account t5 on(t1.ACCOUNT_ID=t5.ID)





       order by  ${orderStr}

