select * from (select
from sf_culprit t1 left join SF_RECIDIVISM r on R.Culprit_Id = t1.id
where t1.group_id = #{groupId,jdbcType=VARCHAR} and r.culprit_id is null and t1.dic_gldxjb_key <> '27005' and t1.dic_status_key = '13001' and t1.id not in (select t.culprit_id from sf_askleave t where ((t.begin_date < #{checkStartTime,jdbcType=TIMESTAMP} and t.end_date > #{checkStartTime,jdbcType=TIMESTAMP}) or (t.begin_date < #{checkEndTime,jdbcType=TIMESTAMP} and t.end_date > #{checkEndTime,jdbcType=TIMESTAMP}) or (t.begin_date > #{checkStartTime,jdbcType=TIMESTAMP} and t.end_date < #{checkEndTime,jdbcType=TIMESTAMP})) and t.dic_status_key <> '17001') order by dbms_random.value) where rownum <= #{amount,jdbcType=VARCHAR}

select 
,t3.name as group_name,t2.DIC_PENAL_TYPE_KEY,t2.CRIME_INFO,t5.ACCOUNTNAME
from SF_CULPRIT t1 left join SF_CULPRIT_JUDGMENT t2 on (t1.ID=t2.CULPRIT_ID) left join sys_group t3 on (t1.group_id=t3.id) left join sys_account t5 on(t1.ACCOUNT_ID=t5.ID)





       order by  ${orderStr}

    select 


	    ,t3.name as group_name, st.mobilenumber as mobileNumber, st.type as type, st.interval_time as intervalTime, st.created as bindtime,	
	    case when st.mobilenumber is null then '未绑定' else '已绑定' end as bindstatus 

    from SF_CULPRIT t1 left join sys_group t3 on (t1.group_id=t3.id) 
    left join sf_terminal st on st.culprit_id = t1.id 






