mapper.select:
select 

from SF_CULPRIT_TJ t1 left join sys_group t2 on (t1.group_id=t2.id)
where t1.ID = #{id,jdbcType=VARCHAR}

select 
,t2.name as group_name
from SF_CULPRIT_TJ t1 left join sys_group t2 on (t1.group_id=t2.id)
where t1.GROUP_ID = #{groupId,jdbcType=VARCHAR} and t1.REPORT_DATE = #{reportDate,jdbcType=TIMESTAMP}

select 

from SF_CULPRIT_TJ t1 left join sys_group t2 on (t1.group_id=t2.id)





       order by  ${orderStr}

select byzcrs from SF_CULPRIT_TJ where group_id=#{groupId,jdbcType=VARCHAR} 
and to_char(created,'yyyymm')>=to_char(add_months(sysdate,-2),'yyyymm')
and to_char(created,'yyyymm')<to_char(add_months(sysdate,-1),'yyyymm')

select count(*) from SF_CULPRIT t left join SF_CULPRIT_JUDGMENT t2 on (t2.CULPRIT_ID=t.id) 
 				where  t.group_id=#{groupId,jdbcType=VARCHAR} 
 				and t2.DIC_PENAL_TYPE_KEY=#{key,jdbcType=VARCHAR}

select count(*) from SF_CULPRIT t left join SF_CULPRIT_JUDGMENT t2 on (t2.CULPRIT_ID=t.id) 
 				where  t.group_id=#{groupId,jdbcType=VARCHAR} 
 				and t2.DIC_CRIME_TYPE_KEY=#{key,jdbcType=VARCHAR}

select count(*) from SF_DETACHED t  left join SF_CULPRIT t2 on (t.CULPRIT_ID=t2.id) 
 				where  t2.group_id=#{groupId,jdbcType=VARCHAR} 
 and to_char(t.DETACHED_TIME,'yyyymm')<to_char(add_months(sysdate,0),'yyyymm')
