mapper.select:
select 

from SYS_DIC
where TAB_NAME = #{tabName,jdbcType=VARCHAR} and COLUMN_NAME = #{columnName,jdbcType=VARCHAR} and KEY = #{key,jdbcType=VARCHAR}

select 

from SYS_DIC
where TAB_NAME = #{tabName,jdbcType=VARCHAR} and COLUMN_NAME = #{columnName,jdbcType=VARCHAR} and value = #{value,jdbcType=VARCHAR}

select 

from SYS_DIC
where TAB_NAME = #{tabName,jdbcType=VARCHAR} and COLUMN_NAME = #{columnName,jdbcType=VARCHAR} and PARENT_KEY=#{parentKey,jdbcType=VARCHAR}

select 

from SYS_DIC t





       order by  ${orderStr}
